import React, { Component } from 'react' ;

export default class AddDep extends Component {
    constructor(props) {
        super(props);

        this.OnChangeName = this.OnChangeName.bind(this);
        this.OnChangeSurame = this.OnChange.bind(this);
        this.OnChangeName = this.OnChangeName.bind(this);
        this.OnChangeName = this.OnChangeName.bind(this);
        this.OnChangeName = this.OnChangeName.bind(this);
        this.OnChangeName = this.OnChangeName.bind(this);
        this.OnChangeName = this.OnChangeName.bind(this);
        this.OnChangeName = this.OnChangeName.bind(this);

        this.state = {
            name : '' ,
            surname :'',
            CIN : '00000000',
            DateNaissance : null ,
            E_mail : '',
            Phone :0,
            DepCible : '',
            PourcTab : [] , 
        }
    }

    onChangeName(e) {
        this.setState({
            name : e.target.value
        })
    }
    onChangeSurame(e) {
        this.setState({
            surname : e.target.value
        })
    }
    onChangeCIN(e) {
        this.setState({
            CIN : e.target.value
        })
    }
    onChangeDateNaiss(e) {
        this.setState({
            DateNaissance : e.target.value
        })
    }
    onChangeMail(e) {
        this.setState({
            E_mail : e.target.value
        })
    }
    onChangePhone(e) {
        this.setState({
            Phone : e.target.value
        })
    }
    onChangeDepCible(e) {
        this.setState({
            DepCible : e.target.value
        })
    }
    onChangePourcTab(a) {
        this.setState({
            PourcTab : this.push(a),
        });
    }

    onSubmit(e) {
        this.setState({
            name : e.target.value
        })
    }


    render() {
        return(
            <div> Helloo 3 </div>
        )
    }
}
