import React, { Component } from 'react' ;


export default class AddDep extends Component {

/*    constructor(props){
        super(props);

        this.OnChangeName = this.OnChangeName.bind(this);
        this.OnChangeCoeff = this.OnChangeCoeff.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            name : '',
            coeff : 0,
        }
    }

  
    */
   
    OnChangeName(e) {
        this.setState({
            name : e.target.value
        });
    }
    OnChangeCoeff(e) {
        this.setState({
            coeff : e.target.value
        });
    }

    OnSubmit(e) {
        e.preventdefault();

        const skill = {
            name : this.state.name,
            coeff : this.state.coeff,
        }
        console.log(skill);

        this.setState({
            name : ''
        })
    }

    render() {
        return(
            
            <div class="container">
                <h2> Veuillez entrer les comp√©tences requises dans votre entreprise : </h2>
                <form action="/action_page.php">
                    <div class="form-group">
                        <label for="SkiillName"> Skill name :</label>
                        <input type="text" class="form-control" id="SkillName" placeholder="Enter skill name" name="SkiillName" />
                    </div>
                    <div class="form-group">
                        <label for="coef"> Coefficient :</label>
                        <input type="number" class="form-control" id="coeff" placeholder="Enter corresponding coefficient" name="coef" /> <br />
                    </div>
                    <button type="submit" class="btn btn-primary"> Add skill </button>
                </form>
            </div>
            
        )
    }
}
