import React, { Component } from 'react' ;
import axios from 'axios';
import SkillArray from './SkillArray.component';

export default class Result extends Component {
    constructor(props) {
        super();

        this.setState({
            SortedDepartement : [],
            Departements : [],
            Candidates : [],
            sortedCandidates : []
        })
    }
        componentDidMount() {
            // import departemnts
            axios.get('localhost:5000/Departements/')
            .then(res => { console.log(res.data) 
                this.setState({
                    Departements : res.data.map(Dep => Dep)
                })

        }) 
            // importing candidates
            axios.get('localhost/5000/Candidates/')
            .then(result => {console.log(result.data)
                this.setState({
                    Candidates : result.data.map(Cand => Cand )
                })
        }) 


           
        }

    
    
    render() {
        return(
            <div> 
               
               
               for (let Dep in Departements) { 
                    Candidates.sort((a,b) => a.ScoreDep.get(Dep.nom) - b.ScoreDep.get(Dep.nom) )}
                    <div className="container"> 
                        <h2>Candidates Model</h2>         
                        <table title={Dep.nom} class="table">
                            <thead>
                            <tr>
                                <th>Firstname</th>
                                <th>Lastname</th>
                                <th>Email</th>
                                <th>Score</th>
                            </tr>
                            </thead>
                        </table>
                    </div>

                    <div>
                    
                        for (let Cand in Candidates)  () => {
                            
                            <div>    
                                <table>
                                    <tbody>
                                    <tr>
                                        <td>{Cand.name}</td>
                                        <td>{Cand.Surname}</td>
                                        <td>{Cand.E_mail}</td>
                                        <td>{Cand.ScoreDep.get(Dep.nom)}</td>

                                    </tr>
                                    </tbody>
                                </table>
                            </div>
                        }
                    
                    }</div>
                            

               
                        
            
            <SkillArray />
            </div> )
           
        
    }
}
