import React, { Component } from 'react' ;
import axios from 'axios';


export default class AddCandid extends Component {
    
    state = {
            name : '' ,
            surname :'',
            CIN : '0',
            DateNaissance : null ,
            E_mail : '',
            Phone :0,
            DepCible : '',
            PourcTab : [] , 
        }

    constructor(props) {
        super(props);

        this.OnChangeName = this.OnChangeName.bind(this);
        this.OnChangeSurame = this.OnChangeSurname.bind(this);
        this.OnChangeCIN = this.OnChangeCIN.bind(this);
        this.OnChangeDateNaiss = this.OnChangeDateNaiss.bind(this);
        this.OnChangeMail = this.OnChangeMail.bind(this);
        this.OnChangePhone = this.OnChangePhone.bind(this);
        this.OnChangeDepCible = this.OnChangeDepCible.bind(this);
        this.OnChangePourcTab = this.OnChangePourcTab.bind(this);
        this.OnSubmit = this.OnSubmit.bind(this);

        
    }

    OnChangeName(e) {
        this.setState({
            name : e.target.value
        })
    }
    OnChangeSurname(e) {
        this.setState({
            surname : e.target.value
        })
    }
    OnChangeCIN(e) {
        this.setState({
            CIN : e.target.value
        })
    }
    OnChangeDateNaiss(e) {
        this.setState({
            DateNaissance : e.target.value
        })
    }
    OnChangeMail(e) {
        this.setState({
            E_mail : e.target.value
        })
    }
    OnChangePhone(e) {
        this.setState({
            Phone : e.target.value
        })
    }
    OnChangeDepCible(e) {
        this.setState({
            DepCible : e.target.value
        })
    }
    OnChangePourcTab(a) {
        this.setState({
            PourcTab : this.PourcTab.push(a),
        });
    }

    OnSubmit(e) {
        e.preventdefault();

        const Cand = {
            name : this.state.name,
            surname : this.state.surname,
            CIN : this.state.CIN,
            DateNaissance : this.state.DateNaissance,
            E_mail : this.state.E_mail,
            Phone : this.state.Phone,
            DepCible : this.state.DepCible,
            PourcTab : this.state.PourcTab,
        }
        console.log(Cand);

        axios.post('http//localhost:5000/Candidates/add',Cand)
        .then(res => console.log(res.data));
    }


    render() {
        return(
            <div className="container">
                <h2> Veuillez entrer les informations et scores relatifs au candidat : </h2>
                {this.state.backendMessage && (
                <div className={`alert alert-${this.state.backendMessageType} alert-dismissible fade show`} role="alert">
                    {this.state.backendMessage}
                    <button type="button" className="close" data-dismiss="alert" aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                    </button>
                </div>
                )}
                <form onSubmit={this.OnSubmit}>
                    <div className="form-group">
                        <label htmlFor="SkiillName"> name :</label>
                        <input type="text" 
                            required
                            className="form-control" 
                            value={this.state.name} 
                            onChange={this.OnChangeName}
                            />
                    </div>
                    <div className="form-group">
                        <label htmlFor="SkiillName"> name :</label>
                        <input type="text" 
                            required
                            className="form-control" 
                            value={this.state.name} 
                            onChange={this.OnChangeName}
                            />
                    </div>
                          
                    <div className="form-group">
                        <label htmlFor="coef"> Coefficient :</label>
                        <input type="number" 
                            required
                            className="form-control"
                            value={this.state.coeff}
                            onChange={this.OnChangeCoeff}
                        /> <br />
                    </div>
                    <button type="submit" className="btn btn-primary"> Add skill </button>
                </form>
            </div>            
        )
    }

}
