import React, { Component } from 'react' ;
import axios from 'axios';

export default class AddDep extends Component {

    constructor(props){
        super(props);

        this.OnChangeName = this.OnChangeName.bind(this);
        this.OnChangeCoeff = this.OnChangeCoeff.bind(this);
        this.OnSubmit = this.OnSubmit.bind(this);

        this.state = {
            name : '',
            coeff : 0,
        }
    }
    
    OnChangeName(e) {
        this.setState( {
            name : e.target.value
        });
    }
    OnChangeCoeff(e) {
        this.setState({
            coeff : e.target.value
        });
    }

    onSubmit(e) {
        e.preventdefault();

        const skill = {
            name : this.state.name,
            coeff : this.state.coeff,
        }
        console.log(skill);

       axios.post('http//localhost:5000/competences/add',skill)
     
        

        this.setState({
            name : '',
            coeff : 0,
        })
    }
    
    
    render() {
        return(
            
            <div class="container">
                <h2> Veuillez entrer les comp√©tences requises dans votre entreprise : </h2>
                <form onSubmit={this.OnSubmit}>
                    <div class="form-group">
                        <label for="SkiillName"> Skill name :</label>
                        <input type="text" 
                            required
                            class="form-control" 
                            id="SkillName"
                            name="SkiillName"
                            onChange={this.OnChangeName}
                            value={this.state.name}  />
                    </div>
                    <div class="form-group">
                        <label for="coef"> Coefficient :</label>
                        <input type="number" 
                            required
                            class="form-control"
                            id="coeff" 
                            name="coef"
                            onChange={this.OnChangeCoeff}
                            value={this.state.coeff}
                        /> <br />
                    </div>
                    <button type="submit" class="btn btn-primary"> Add skill </button>
                </form>
            </div>
            
            )
        }
    }
