import React, { Component } from 'react' ;


export default class AddDep extends Component {
    constructor(props) {
        super();

        this.onChangeName = this.onChangeName.bind(this);
        this.onChangeBesoin = this.onChangeBesoin.bind(this);
        this.onChangeCompReq = this.onChangeCompReq.bind(this);


        this.state = {
            name : '',
            besoin : 0,
            CompReq : new Array(),
            CompSupp : new Array(),
        }
    }

    onChangeName(e) {
        this.setState({
            name : e.target.value
        });
    }
    onChangeBesoin(e) {
        this.setState({
            besoin : e.target.value
        });
    }
    
    onChangeCompReq(a) {
        this.setState({
            CompReq : CompReq.push(e),
        });
    }

    onSubmit(e) {
        e.preventDefault();

        const Dep = {
            name : e.state.name,
            besoin : e.state.besoin,
            CompReq : CompReq.push(e),
        }
    }
    
    
    
    render() {
        return(
            <div class="container">
                <h2> Veuillez entrer les Departements de votre entreprise et les compétences correspondantes  : </h2>
                <form onSubmit={this.onSubmit()} action="/action_page.php">
                    <div class="form-group">
                        <label for="DeppName"> Departement name :</label>
                        <input type="text" class="form-control" id="DepName" placeholder="Enter Departement name" name="DeppName" />
                    </div>
                    <div class="form-group">
                        <label for="BesoinHR"> Nombre de ressources humaines demandées dans ce departement :</label>
                        <input type="number" className="form-control" id="BesoinHRR" placeholder=" number of HR " name="BesoinHR" /> <br />
                    </div>
                    <div class="form-group">
                        <label for="CReq"> Compétences requises dans ce departement :</label> <br />
                        {
                            this.state.competences.map(function(skill){
                                return (
                                    <input 
                                        type="checkbox"
                                        key={skill}
                                        value={skill}
                                        onCheck={onChange}>
                                    {skill}            
                                    </input>
                                    )
                            })
                        }
                       
                    </div>
                    <button type="submit" class="btn btn-primary"> Add skill </button>
                </form>
            </div>
        )
    }
}
