import axios from 'axios';
import React, { Component } from 'react' ;

// Beginner way of importing js file into react component
//import ScriptTag from 'react-script-tag';



export default class AddDep extends Component {     
    
    constructor(props) {
        super();
/*
        const Demo = props => (
            <ScriptTag type="text/javascript" src="../../../back/routes/Admin/Human-Resources/ArraySkill.js" />
            )
*/

        this.state = {
            name : '',
            besoin : 0,
            skills : [], // array of all skills imported from database to insert in the checkboxes
            CompRequis : [],
            CompSupp : [],
        }
        this.onChangeName = this.onChangeName.bind(this);
        this.onChangeBesoin = this.onChangeBesoin.bind(this);
        this.onChangeCompReq = this.onChangeCompReq.bind(this);
        this.onChangeCompSupp = this.onChangeCompSupp.bind(this);
        this.OnSubmit = this.OnSubmit.bind(this);

    }
    


    componentDidMount() {
        // Display the loading image
        // Fetch the user profile info from the backend
        
        // Save info to state.user
        // Remove the loading image

        axios.get('http://localhost:5000/competences/')
            .then(result =>{ console.log('uuuup');
            console.log(result.data);
                //if (result.data.length > 0) {
                    this.setState({
                        skills : result.data 
                        //skills : result.data.map(skill => skill.nom)
                    })

                    console.log('middle');
                    console.log(this.skills);
                //}
                console.log('Dooown');
                console.log(this.skills)
            }
            )
        
            //CompRequis : [{name :'Problem solving', coeff : 7}]
        }
    

    onChangeName(e) {
        this.setState({
            name : e.target.value
        });
    }
    onChangeBesoin(e) {
        this.setState({
            besoin : e.target.value
        });
    }
    
    onChangeCompReq(a) {
        this.setState({
            CompRequis : this.CompRequis.push(a),
        });
    }
    onChangeCompSupp(a) {
        this.setState({
            CompSupp : this.CompSupp.push(a),
        });
    }

    OnSubmit(e) {
        e.preventDefault();

        const Dep = {
            name : this.state.name,
            besoin : this.state.besoin,
            CompRequis : this.state.CompRequis,
        }

        axios.post('http://localhost:5000/Departements/add',Dep)
        .then(res => {console.log(res.data)});

        this.setState = {
            name : this.state.name,
            besoin : this.state.besoin,
            CompRequis : [],
            CompSupp : [],
        }
    }
    
    
    render() {
        return(
            <div className="container">
                <h2> Veuillez entrer les Departements de votre entreprise et les compétences correspondantes  : </h2>
                <form onSubmit={this.OnSubmit} >
                    <div className="form-group">
                        <label htmlFor="DeppName"> Departement name :</label>
                        <input type="text" className="form-control" 
                        value={this.state.name}
                        onChange={this.onChangeName} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="BesoinHR"> Nombre de ressources humaines demandées dans ce departement :</label>
                        <input type="number" className="form-control"
                                value={this.state.besoin}
                                onChange={this.onChangeBesoin} 
                        /> <br />
                    </div>
                    <div className="form-group">
                    <label htmlFor="CReq"> Compétences requises dans ce departement :</label> <br />
                    <div>
                        { this.skills.map((Competence, index) => { return (
                            <div key={index}> 
                                <input type="Checkbox"
                                        value={Competence}
                                        onc={this.state.CompRequis}>
                                            {Competence.nom}
                                </input>
                            </div>    
                        )
                        }) 
                        }  
                    </div>

                    <label htmlFor="CReq"> Compétences supplementaires pour ce departement :</label> <br />
                    <div>
                        { /*this.skills.map(Competence => {
                            <input type="Checkbox"
                            oncheck={this.state.CompSupp}>
                                {Competence.nom}
                            </input>
                        })*/
                        }  
                    </div>
                                  
                            
                        
                       
                    </div>
                    <button type="submit" className="btn btn-primary"> Add departement </button>
                </form>
            </div>
    
    )
    }
    
}
