import axios from 'axios';
import React, { Component } from 'react' ;


export default class AddDep extends Component {
     constructor(props) {
        super();

        this.onChangeName = this.onChangeName.bind(this);
        this.onChangeBesoin = this.onChangeBesoin.bind(this);
        this.onChangeCompReq = this.onChangeCompReq.bind(this);
        this.onSubmit = this.onSubmit.bind(this);


        this.state = {
            name : '',
            besoin : 0,
            CompRequis : [],
            CompSupp : [],
        }
    }
    
    componentDidMount() {
        this.setState({
            CompRequis : [{name :'Problem solving', coeff : 7}]
        })
    }

    onChangeName(e) {
        this.setState({
            name : e.target.value
        });
    }
    onChangeBesoin(e) {
        this.setState({
            besoin : e.target.value
        });
    }
    
    onChangeCompReq(a) {
        this.setState({
            CompRequis : this.CompRequis.push(a),
        });
    }

    onSubmit(e) {
        e.preventDefault();

        const Dep = {
            name : this.state.name,
            besoin : this.state.besoin,
            CompRequis : this.state.CompRequis,
        }

        axios.post('http://localhost:5000/Departements')
        /*this.setState = {
            name : this.state.name,
            besoin : this.state.besoin,
            CompRequis : [],
            //CompSupp : [],
        }*/
    }
    
    
    render() {
        return(
            <div className="container">
                <h2> Veuillez entrer les Departements de votre entreprise et les compétences correspondantes  : </h2>
                <form onSubmit={this.onSubmit} >
                    <div className="form-group">
                        <label for="DeppName"> Departement name :</label>
                        <input type="text" className="form-control" 
                        value={this.state.name}
                        onChange={this.onChangeName} />
                    </div>
                    <div className="form-group">
                        <label for="BesoinHR"> Nombre de ressources humaines demandées dans ce departement :</label>
                        <input type="number" className="form-control"
                        value={this.state.besoin}
                        onChange={this.onChangeBesoin} 
                        /> <br />
                    </div>
                    <div className="form-group">
                        
                       
                    </div>
                    <button type="submit" className="btn btn-primary"> Add departement </button>
                </form>
            </div>
    
    )
    }
    
}
