import React, { Component } from 'react' ;
import axios from 'axios';
import SkillArray from './SkillArray.component';

export default class Result extends Component {
    constructor(props) {
        super();

        this.setState({
            SortedDepartement : [],
            Departements : [],
            Candidates : [],
            sortedCandidates : []
        })
    }
        componentDidMount() {
            // import deparetemnts
            axios.get('localhost:5000/Departements/')
            .then(res => { console.log(res.data) 
                this.setState({
                    Departements : res.data.map(Dep => Dep)
                })

        }) 
            // importing candidates
            axios.get('localhost/5000/Candidates/')
            .then(result => {console.log(result.data)
                this.setState({
                    Candidates : result.data.map(Cand => Cand )
                })
        }) 


            // sorting 
            for (let Dep in Departements) {
                Candidates.sort((a,b)=> a.ScoreDep.get(Dep.nom) - b.ScoreDep.get(Dep.nom) );
                <div class="container">
                    <h2>Basic Table</h2>
                    <p>The .table class adds basic styling (light padding and horizontal dividers) to a table:</p>            
                    <table class="table">
                        <thead>
                        <tr>
                            <th>Firstname</th>
                            <th>Lastname</th>
                            <th>Email</th>
                            <th>Score</th>
                        </tr>
                        </thead>
                        <tbody>
                        <tr>
                            <td>John</td>
                            <td>Doe</td>
                            <td>john@example.com</td>
                        </tr>
                        
                        </tbody>
                    </table>
                </div>
            }

        }

    
    
    render() {
        return(
            <div> Helloo 0 

                

            <SkillArray />
            </div>
        )
    }
}
