import React, { Component } from 'react' ;
import axios from 'axios';

export default class AddDep extends Component {

    constructor(props){
        super(props);

        this.OnChangeName = this.OnChangeName.bind(this);
        this.OnChangeCoeff = this.OnChangeCoeff.bind(this);
        this.OnSubmit = this.OnSubmit.bind(this);

        this.state = {
            name : '',
            coeff : 0,
        }
    }
    
    OnChangeName(e) {
        this.setState( {
            name : e.target.value
        });
    }
    OnChangeCoeff(e) {
        this.setState({
            coeff : e.target.value
        });
    }

    onSubmit(e) {
        e.preventdefault();

        const skill = {
            name : this.state.name,
            coeff : this.state.coeff,
        }
        console.log(skill);

        axios.post('http//localhost:5000/competences/add',skill)
        .then(res => console.log(res.data));
        

        this.setState({
            name : '',
            coeff : 0,
        })
    }
    
    
    render() {
        return(
            
            <div className="container">
