import React, { Component } from 'react' ;
import axios from 'axios';

export default class AddComp extends Component {

    

    constructor(props){
        super(props);

        this.OnChangeName = this.OnChangeName.bind(this);
        this.OnChangeCoeff = this.OnChangeCoeff.bind(this);
        this.OnSubmit = this.OnSubmit.bind(this);
    }

    //componentDidMount
    
    OnChangeName(e) {
        this.setState( {
            name : e.target.value
        });
    }
    OnChangeCoeff(e) {
        this.setState({
            coeff : e.target.value
        });
    }

    OnSubmit(e) {
        e.preventDefault();

        console.info("foo", this.state.name);

        // const skill = {
        //     name : this.state.name,
        //     coeff : this.state.coeff,
        // }
        // console.log(skill);

        // axios.post('http//localhost:5000/competences/add',skill)
        // .then(res => {console.log(res.data)});
        

        // this.setState({
        //     name : '',
        //     coeff : 0 ,
        // })
    }
    
    
    render() {
        return(
            
            <div className="container">
                <h2> Veuillez entrer les comp√©tences requises dans votre entreprise : </h2>
                <form onSubmit={this.OnSubmit}>
                    <div className="form-group">
                        <label htmlFor="SkiillName"> Skill name :</label>
                        <input type="text" 
                            required
                            className="form-control" 
                            value={this.state.name} 
                            onChange={this.OnChangeName}
                            />
                    </div>
                          
                    <div className="form-group">
                        <label htmlFor="coef"> Coefficient :</label>
                        <input type="number" 
                            required
                            className="form-control"
                            value={this.state.coeff}
                            onChange={this.OnChangeCoeff}
                        /> <br />
                    </div>
                    <button type="submit" className="btn btn-primary"> Add skill </button>
                </form>
            </div>
            
            )
        }
    }
