import React, { Component } from 'react' ;
import axios from 'axios';


export default class AddCandid extends Component {
    
    state = {
            name : '' ,
            surname :'',
            CIN : '0',
            DateNaissance : null ,
            E_mail : '',
            Phone :0,
            DepCible : '',
            PourcTab : [] , 
        }

    constructor(props) {
        super(props);

        this.OnChangeName = this.OnChangeName.bind(this);
        this.OnChangeSurame = this.OnChangeSurname.bind(this);
        this.OnChangeCIN = this.OnChangeCIN.bind(this);
        this.OnChangeDateNaiss = this.OnChangeDateNaiss.bind(this);
        this.OnChangeMail = this.OnChangeMail.bind(this);
        this.OnChangePhone = this.OnChangePhone.bind(this);
        this.OnChangeDepCible = this.OnChangeDepCible.bind(this);
        this.OnChangePourcTab = this.OnChangePourcTab.bind(this);
        this.OnSubmit = this.OnSubmit.bind(this);

        
    }

    nChangeName(e) {
        this.setState({
            name : e.target.value
        })
    }
    nChangeSurame(e) {
        this.setState({
            surname : e.target.value
        })
    }
    nChangeCIN(e) {
        this.setState({
            CIN : e.target.value
        })
    }
    nChangeDateNaiss(e) {
        this.setState({
            DateNaissance : e.target.value
        })
    }
    nChangeMail(e) {
        this.setState({
            E_mail : e.target.value
        })
    }
    nChangePhne(e) {
        this.setState({
            Phne : e.target.value
        })
    }
    onChangeDepCible(e) {
        this.setState({
            DepCible : e.target.value
        })
    }
    onChangePourcTab(a) {
        this.setState({
            PourcTab : this.PourcTab.push(a),
        });
    }

    onSubmit(e) {
        e.preventdefault();

        const Cand = {
            name : this.state.name,
            surname : this.state.surname,
            CIN : this.state.CIN,
            DateNaissance : this.state.DateNaissance,
            E_mail : this.state.E_mail,
            Phone : this.state.Phone,
            DepCible : this.state.DepCible,
            PourcTab : this.state.PourcTab,
        }
        console.log(Cand);

        axios.post('http//localhost:5000/Candidates/add',Cand)
        .then(res => console.log(res.data));
    }


    render() {
        return(
            <div> Helloo 3 </div>
        )
    }

}
